import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'
import './App.css';


function EditWatch({ name, id, modelNum, price, imageUrl }) {

    const [watchName, setWatchName] = useState(name);
    // const [watchId, setWatchId] = useState(id);
    const [watchModelNum, setModelNum] = useState(modelNum);
    const [watchPrice, setPrice] = useState(price);
    const [watchImageUrl, setImageUrl] = useState(imageUrl);

    function handleNameChange(e) {
        setWatchName(e.target.value)
    }
    function handleUrlChange(e) {
        console.log('change', e.target.value)
        setImageUrl(e.target.value)
    }
    function handlePriceChange(e) {
        setPrice(e.target.value)
    }
    function handleModelNum(e) {
        setModelNum(e.target.value)
    }


    const handleSubmit = (e) => {
        e.preventDefault()
        fetch(`http://localhost:9292/brands/${brandId}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                name: name,
                image_url: image_url,
                price: price,
                model_num: modelNum,
                brand_id: brandId
            })

        })
            .then(response => response.json())
            .then(data => {

                //need to send the return object up to App to update State 
                console.log('Success:', data);
                addNewWatchToStateArr(data)
                // setWatchFormData(
                //     {
                //         name: "",
                //         image_url: "",
                //         price: "",
                //         model_num: "",
                //         brand_id: brandId
                //     }
                // )
            })

    }




    return (
        <div>
            <form onSubmit={handleSubmit}>

                <label>
                    Name:
                    <input type="text" name='name' onChange={handleNameChange} value={name} />
                </label>
                Price:
                <input type="text" name='price' onChange={handlePriceChange} value={price} />
                Image Url Link:
                <input type="text" name='imageUrl' onChange={handleUrlChange} value={image_url} />
                Model Number:
                <input type="text" name='model_num' onChange={handleModelNum} value={modelNum} />

                <button type="submit">Submit</button>

            </form>

        </div>


    )

};



export default EditWatch;
