                    Name:
                    <input type="text" name='name' onChange={handleNameChange} value={name} />
                </label>
                Price:
                <input type="text" name='price' onChange={handlePriceChange} value={price} />
                Image Url Link:
                <input type="text" name='imageUrl' onChange={handleUrlChange} value={imageUrl} />
                Model Number:
